mapper.select:
SELECT counselor_id, name, title, supervisor, supervisor_id, session_count, session_time, session_score, banned FROM (SELECT uc.id as counselor_id, uc.name as name, uc.title as title, us.name as supervisor, us.id as supervisor_id, uc.banned as banned FROM user_counselor as uc LEFT JOIN binding ON uc.id=binding.counselor_id LEFT JOIN user_supervisor as us ON us.id=binding.supervisor_id
uc.name = #{name}
) AS t1 LEFT JOIN (SELECT counselor_id as cid, SUM(`session`.counselor_id) as session_count, SUM(`session`.duration) as session_time, SUM(`session`.visitor_feedback_score)/SUM(`session`.counselor_id) as session_score FROM `session` GROUP BY `session`.counselor_id ) AS t2 ON t1.counselor_id = t2.cid
